import React, { useState } from "react";
import SortByDate from "./SortByDate/SortByDate";
import SortByStatus from "./SortByDone/SortByStatus";

import style from './SortTask.module.css';

const SortTask = ({ taskList, setTaskList, filtredTodoList, setFiltredTodoList,
    setCurretnPage, sort, setTypeOfSorted, typeOfSorted }) => {

    return (
        <div className={style.controlPanel}>
            <SortByStatus
                setCurretnPage={setCurretnPage}
                setTypeOfSorted={setTypeOfSorted}
                typeOfSorted={typeOfSorted}
            // taskList={taskList} filtredTodoList={filtredTodoList}
            // setFiltredTodoList={setFiltredTodoList} sort={sort}
            />
            <SortByDate taskList={taskList} setTaskList={setTaskList}
                filtredTodoList={filtredTodoList}
                setTypeOfSorted={setTypeOfSorted} typeOfSorted={typeOfSorted}

            />
        </div>
    )
};

export default SortTask;
