import React, { useState } from "react";
import SortByDate from "./SortByDate/SortByDate";
import SortByStatus from "./SortByDone/SortByStatus";

import style from './SortTask.module.css';

const SortTask = ({props, taskList, setTaskList, filtredTodoList, setFiltredTodoList, setCurretnPage, sort, setTypeOfSorted}) => {
    
    return (
        <div className={style.controlPanel}>
            <SortByStatus taskList={taskList} filtredTodoList={filtredTodoList}
                setFiltredTodoList={setFiltredTodoList}
                setCurretnPage={setCurretnPage} 
                sort={sort} setTypeOfSorted={setTypeOfSorted} typeOfSorted={props.typeOfSorted}
    
                />
            <SortByDate taskList={taskList} setTaskList={setTaskList}
                filtredTodoList={filtredTodoList} 
                setTypeOfSorted={setTypeOfSorted} typeOfSorted={props.typeOfSorted}
    
                 />
        </div>
    )
};

export default SortTask;
