import React from "react";
import MyButton from "../../Button/MyButton";
import style from './SortByDate.module.css';

const SortByDate = (props) => {
    console.log(props.taskList);
    const sortByDate = (taskList) => {
        setTaskList([...taskList].sort((a, b) => a[miliTaskDate] - b[miliTaskDate] ))
      }
    
    return (
        <div className='sortDatePanel'>
            Sort by date
            <MyButton onClick={sortByDate}>⇧</MyButton> 
            <MyButton>⇩</MyButton> 
        </div>
    )
}

export default SortByDate;
