import React from "react";
import MyButton from "../../Button/MyButton";
import style from './SortByStatus.module.css';

const optionSortByDone = ['All', 'Done', 'Undone']


const SortByStatus = (props) => {

    const sortByStatus = (taskList) => {
        const copyTaskList = taskList.filter(item => item.isTaskDone === true);
        return console.log(copyTaskList);
    }






    return (
        <div className={style.sortPanel}>
            {/* <MyButton>All</MyButton> */}
            <button onClick={() => sortByStatus(props.taskList)}>сортировать по готовности</button>
            {/* <MyButton >Done</MyButton> */}
            {/* <MyButton>Undone</MyButton> */}
        </div>
    )
}

export default SortByStatus;
