import React from "react";
import { useState } from "react";
import Button from "../Button/Button";
import style from './InputTask.module.css';
const someFunction = () => {
    console.log('Click Add Task')
}



const InputTask = (props) => {
    
    const addNewTask = () => {
        const newTask = { 
            id: Date.now(),
            taskText:'' , 
            taskDate: '10/04/2022' };
props.setTasks([...props.taskList,])
    }
    
    const [taskText, setTaskText] = useState('');
    return (
        <div className={style.inputTask}>
            <input type="text" className={style.inputTask2} placeholder="I want to..." value={taskText}
                onChange={event => setTaskText(event.target.value)} />
            <Button onChange={addNewTask}>Add Task</Button>
        </div>
    )
}

export default InputTask;
