import React from "react";
import MyButton from "../../Button/MyButton";
import style from './SortByDate.module.css';

const SortByDate = (props) => {

    const sortByDate = (taskList) => {
        console.log(taskList);
        const sortOfTasks = [...taskList].sort((a, b) => a.miliTaskDate - b.miliTaskDate)
        props.setTaskList(sortOfTasks);
    }

    return (
        <div className='sortDatePanel'>
            Sort by date
            <MyButton onClick={() => sortByDate(props.taskList)}>⇧</MyButton>
            <MyButton>⇩</MyButton>
        </div>
    )
}

export default SortByDate;
